import React, {Component} from 'react';
import  './SearchingForm.css';
import axios from 'axios';
import DatePicker from 'react-pikaday-datepicker';
import AutoComplete from "./AutoComplete";



class SearchingForm extends Component {

    items=[
        "London",
        "Paris",
        "Budapest",
        "BUD",
        "AMS",
        "Amsterdam",
        "Prague",
        "Pula"
    ];

    state ={
        from:'',
        to:'',
        from_date:'',
        suggestions: []
    };

    onChange = (e) => {
        this.setState({[e.target.name]: e.target.value});
    };

    onSubmit = (e) =>{
        e.preventDefault();

        axios.get(`http://localhost:8080/getfromtoprice/${this.state.from}/${this.state.to}/${this.state.from_date}/`, this.state)
            .then(res => {
                    console.log(res.data)
                this.props.sendDataToParent(res.data)
                }
            )
            };

    onTextChange = (e) => {
        const value = e.target.value;
        const keyName = e.target.name;
        let suggestions = [];
        if (value.length > 0){
            const regex = new RegExp(`^${value}`, 'i');
            suggestions = this.items.sort().filter(v => regex.test(v));
        }
        this.setState(() => ({suggestions, [keyName]: value}));
    };

    renderSuggestionsFrom () {
        const {suggestions} = this.state;
        if (suggestions.length === 0){
            return null;
        } else{
            return(
            <ul>
                {suggestions.map((item) => <li onClick={() => this.suggestionSelectedFrom(item)}>{item}</li>)}
            </ul>)
        }
    }

    renderSuggestionsTo () {
        const {suggestions} = this.state;
        if (suggestions.length === 0){
            return null;
        } else{
            return(
                <ul>
                    {suggestions.map((item) => <li onClick={() => this.suggestionSelectedTo(item)}>{item}</li>)}
                </ul>)
        }
    }
    suggestionSelectedFrom(value){
        this.setState(() =>({
            from: value,
            suggestions:[]
        }))
    }

    suggestionSelectedTo(value){
        this.setState(() =>({
            to: value,
            suggestions:[]
        }))
    }

    handleChange = (date) => {
        this.setState({startDate: date})};


    render() {
        return (
            <form onSubmit={this.onSubmit} className="form">
                <div className="container">
                <AutoComplete value="from" placeholder="From"/>
                <AutoComplete value="to" placeholder="To"/>
                {/*<input className="input" type="text"  name="from" placeholder="From" value={this.state.from} onChange={this.onTextChange}/>*/}
                {/*{this.renderSuggestionsFrom()}*/}
                <input className="input" type="text"  name="to" placeholder="To" value={this.state.to} onChange={this.onTextChange}/>
                {this.renderSuggestionsTo()}
                <input className="input" type="text"  name="from_date"  placeholder="Departure date: YYYY-MM-DD" value={this.state.from_date} onChange={this.onChange}/>
                <input type="submit" value="Submit" className="btn"/>
                </div>
            </form>
        )
    }
}

export default SearchingForm;
