import React, {Component} from 'react';

class AutoComplete extends Component{

    constructor(props){
        super(props);
        this.items=[
            'London',
            "Paris",
            "Budapest",
            "BUD",
            "AMS",
            "Amsterdam",
            "Prague",
            "Pula"
        ];
        this.state={
            suggestions: []
        }
    }


    onTextChange = (e) => {
        const value = e.target.value;
        if (value.length > 0){
            const regex = new RegExp(`^${value}`, "i");
        }
    }


    render(){
        return(
            <div>
                <input onChange={this.onTextChange} type="text" placeholder={this.props.placeholder}/>
                <ul>
                    {this.items.map((item) => <li>{item}</li>)}
                </ul>
            </div>
        )
    }

}

export default AutoComplete;
